mapper.insert:
INSERT INTO board
SET title = #{title}
, contents = #{contents}
, writer_id = #{writerId}

mapper.select:
SELECT board_id AS boardId, title, contents, writer_id AS writerId, hits
FROM board
WHERE board_id = #{boardId}

mapper.update:
UPDATE board
SET updated_at = NOW(),
title = #{title} ,
    contents = #{contents}
WHERE board_id = #{boardId}
